family inet {
     filter egress-filter {
          term icmp-out-good {
               from {
                    source-address {
                         10.0.0.0/24;
                    }
                    protocol icmp;
                    icmp-type [ echo-request source-quench ];
               }
               then {
                    accept;
               }
          }

          term icmp-out-bad {
               from {
                    protocol icmp;
               }
               then {
                    syslog;
                    discard;
               }
           }
      }
}

