         family inet {
            filter {
               output OOBM-egress-filter;
            }
            address 10.1.20.3/24;
         }
      }   
   }
}
firewall {
   filter OOBM-egress-filter {
      term allow-mgmt {
         from {
            source-address {
               10.1.1.0/24;
            }
            destination-address {
               10.2.2.0/24;
            }
         }
         then {
            accept;
         }
      }
          …
          …
          …
      term default-action {
         then {
            syslog;
            discard;
         }
      }
   }
 }

